import React, { Component } from "react";
import { Redirect, Route } from "react-router-dom";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import "./login.css";

class Login extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
    };
    this.onSubmit = this.onSubmit.bind(this);
  }

  getLoginData = (value, type) =>
    this.setState({
      [type]: value,
    });
  onSubmit = (e) => {
    console.log(this.state.email);
    if (this.state.email === "" && this.state.password === "") {
      this.props.history.push("/counters");
    } else {
      alert("Register to sign in");
    }
  };
  render() {
    return (
      <div className="Login">
        <form>
          <FormGroup controlId="email" bsSize="large">
            <ControlLabel>Email</ControlLabel>
            <FormControl
              autoFocus
              type="email"
              getValue={(value) => this.getLoginData(value, "email")}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large">
            <ControlLabel>Password</ControlLabel>
            <FormControl
              getValue={(value) => this.getLoginData(value, "password")}
              type="password"
            />
          </FormGroup>
          <Button block bsSize="large" type="submit" onClick={this.onSubmit}>
            Login
          </Button>
        </form>
      </div>
    );
  }
}

export default Login;
