import React, { Component } from "react";

class Counter extends Component {
  state = {
    imageUrl:
      "https://www.google.com/url?sa=i&url=https%3A%2F%2Funsplash.com%2Fs%2Fphotos%2Fsunrise&psig=AOvVaw2KYV06ZN17WhlEuSUXiOgO&ust=1598041540919000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCOiz6-bOqusCFQAAAAAdAAAAABAD",
    count: 0,
  };

  render() {
    return (
      <React.Fragment>
        <img src="https://www.google.com/url?sa=i&url=https%3A%2F%2Funsplash.com%2Fs%2Fphotos%2Fsunrise&psig=AOvVaw2KYV06ZN17WhlEuSUXiOgO&ust=1598041540919000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCOiz6-bOqusCFQAAAAAdAAAAABAD" />
        <span>{this.formatCount()}</span>
        <br></br>
        <button>increment</button>
      </React.Fragment>
    );
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "zero" : count;
  }
}

export default Counter;
