import React, { Component } from "react";
import "./login.css";
import Counters from "./counters";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";

class Login extends Component {
  state = {
    email: "",
    password: "",
  };
  constructor() {
    super();
    this.onSubmit = this.onSubmit.bind(this);
  }
  changeHandler = (event) => {
    let name = event.target.name;
    console.log(name);
    this.setState({ [name]: event.target.value });
  };
  onSubmit = () => {
    console.log(this.state.email);
    if (
      this.state.email === "pavansaikuramsetti8@gmail.com" &&
      this.state.password === "pavan"
    ) {
      this.props.history.push("/counters");
    } else {
      alert("Register to sign in");
    }
  };
  render() {
    return (
      <div className="Login">
        <form>
          <input
            type="text"
            name="email" // <-- event.target.name is set to "name" here
            placeholder="email"
            onChange={this.changeHandler}
          />
          <input
            type="password"
            name="password" // <-- event.target.name is set to "name" here
            placeholder="password"
            onChange={this.changeHandler}
          />
          <br></br>
          <button block bsSize="large" type="submit" onClick={this.onSubmit}>
            Login
          </button>
          <Route exact path="/counters" component={Counters} />
        </form>
      </div>
    );
  }
}

export default Login;
