import React, { Component } from "react";
import { Redirect, Route } from "react-router-dom";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import "./login.css";

class Login extends Component {
  state = {
    email: "",
    password: "",
  };
  constructor() {
    super();
    this.onSubmit = this.onSubmit.bind(this);
  }
  changeHandler = (event) => {
    let name = event.target.name;
    console.log(name);

    // this.state.email = event.target.value;
    this.setState({ [name]: event.target.value });
    console.log(this.state.email);
  };
  onSubmit = (e) => {
    console.log(this.state.email);
    if (this.state.email === "pavansaikuramsetti8@gmail.com") {
      this.props.history.push("/counters");
    } else {
      alert("Register to sign in");
    }
  };
  render() {
    return (
      <div className="Login">
        <form>
          <input
            type="text"
            name="email" // <-- event.target.name is set to "name" here
            placeholder="email"
            onChange={this.changeHandler}
          />
          <Button block bsSize="large" type="submit" onClick={this.onSubmit}>
            Login
          </Button>
        </form>
      </div>
    );
  }
}

export default Login;
