import React, { Component } from "react";
import { Redirect, Route } from "react-router-dom";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import "./login.css";

class Login extends Component {
  state = {
    email: "",
    password: "",
  };

  onSubmit(e) {
    e.preventDefault();

    const { email, password } = this.state;
    const validEmailRegex = RegExp(
      /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
    );
    if (!validEmailRegex.test(email)) {
      alert("Type a valid email id");
    } else if (password.length < 5) {
      alert("Password should be of atleast 5 characters length");
    } else {
      if (this.state.email === "pavansai" && this.state.password === "pavan") {
        this.props.history.push("/counters");
      } else {
        alert("Register to sign in");
      }
    }
  }
  render() {
    return (
      <div className="Login">
        <form>
          <FormGroup controlId="email" bsSize="large">
            <ControlLabel>Email</ControlLabel>
            <FormControl
              autoFocus
              type="email"
              value={this.state.email}
              onChange={(event, newValue) => this.setState({ email: newValue })}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large">
            <ControlLabel>Password</ControlLabel>
            <FormControl
              value={this.state.password}
              getValue={(value) => this.getLoginData(value, "password")}
              onChange={(event, newValue) =>
                this.setState({ password: newValue })
              }
              type="password"
            />
          </FormGroup>
          <Button block bsSize="large" type="submit" onClick={this.onSubmit}>
            Login
          </Button>
        </form>
      </div>
    );
  }
}

export default Login;
